            if (bottomNav != null) {
                // æ— è®ºå·¦æ‰‹è¿˜æ˜¯å³æ‰‹æ¨¡å¼ï¼Œéƒ½ä¿æŒLTRæ–¹å‘ï¼Œç¡®ä¿tabé¡ºåºä¸€è‡´
                bottomNav.layoutDirection = View.LAYOUT_DIRECTION_LTR
