    private fun setupAppSearchPage() {
        Log.d(TAG, "setupAppSearchPageè¢«è°ƒç”¨")
        Log.d(TAG, "appSearchAdapteræ˜¯å¦å·²åˆå§‹åŒ–: ${::appSearchAdapter.isInitialized}")
        
