    private fun performDirectSearch() {
        val query = directSearchInput.text.toString().trim()
        if (query.isNotEmpty()) {
            // ä½¿ç”¨å†…åµŒæµè§ˆå™¨è¿›è¡Œæœç´¢
            showBrowser()
            browserSearchInput.setText(query)
            performBrowserSearch()
        }
    }

    private fun setupAIAssistantCenter() {
        try {
