    /**
     * è®¾ç½®WebViewæ»šåŠ¨ç›‘å¬å™¨
     */
    private fun setupWebViewScrollListener() {
        try {
            // ä¸ºç°æœ‰çš„WebViewç®¡ç†å™¨è®¾ç½®å›è°ƒï¼Œåœ¨WebViewåˆ›å»ºæ—¶æ·»åŠ æ»šåŠ¨ç›‘å¬
            gestureCardWebViewManager?.setOnWebViewCreatedListener { webView ->
