            addScrollListenerToExistingWebViews()

            Log.d(TAG, "WebViewæ»šåŠ¨ç›‘å¬å™¨è®¾ç½®å®Œæˆ")

        } catch (e: Exception) {
            Log.e(TAG, "è®¾ç½®WebViewæ»šåŠ¨ç›‘å¬å™¨å¤±è´¥", e)
