    private fun showEditPresetTextDialog(currentText: String, chipId: Int) {
        try {
            val input = android.widget.EditText(this).apply {
                setText(currentText)
                hint = "è¾“å…¥æ–°çš„é¢„è®¾æ–‡æœ¬"
                selectAll()
