    private fun setupCardPreviewFeature() {
        // åˆ›å»ºå¡ç‰‡é¢„è§ˆè¦†ç›–å±‚
        cardPreviewOverlay = CardPreviewOverlay(this)
        cardPreviewOverlay.layoutParams = FrameLayout.LayoutParams(
