            Toast.makeText(this, "æ²¡æœ‰æ‰“å¼€çš„é¡µé¢", Toast.LENGTH_SHORT).show()
        }

        Log.d(TAG, "=== å·¦ä¸Šè§’å¡ç‰‡é¢„è§ˆç»“æŸ ===")
