            val mobileCards = mobileCardManager?.getAllCards() ?: emptyList()
            val paperStackTabs = paperStackWebViewManager?.getAllTabs() ?: emptyList()
            val allCards = mutableListOf<GestureCardWebViewManager.WebViewCardData>()

