            // å†æ·»åŠ æ‰‹æœºå¡ç‰‡ï¼Œé¿å…é‡å¤
            var duplicateCount = 0
            mobileCards.forEach { mobileCard ->
                // æ£€æŸ¥æ˜¯å¦å·²å­˜åœ¨ç›¸åŒçš„å¡ç‰‡ï¼ˆé€šè¿‡URLæˆ–IDåˆ¤æ–­ï¼‰
