            }

            Log.d(TAG, "ç»Ÿä¸€å¡ç‰‡æ•°æ® - æ‰‹åŠ¿å¡ç‰‡: ${gestureCards.size}, æ‰‹æœºå¡ç‰‡: ${mobileCards.size}, çº¸å †æ ‡ç­¾é¡µ: ${paperStackTabs.size}, é‡å¤: $duplicateCount, å»é‡åæ€»è®¡: ${allCards.size}")
            Log.d(TAG, "=== ç»Ÿä¸€å¡ç‰‡æ•°æ®è·å–ç»“æŸ ===")

            return allCards
        } catch (e: Exception) {
