                    settingsManager.putBoolean(key, true)
                    refreshHomeButtonVisibility()
                    showMaterialToast("å·²æ¢å¤æŒ‰é’®")
                    // å…³é—­å¯¹è¯æ¡†
                    dialog.dismiss()
                }
                
                container?.addView(itemView)
            }
            
            dialog.show()
        } catch (e: Exception) {
            Log.e(TAG, "æ˜¾ç¤ºæ¢å¤æŒ‰é’®å¯¹è¯æ¡†å¤±è´¥", e)
            showMaterialToast("æ‰“å¼€æ¢å¤æŒ‰é’®å¤±è´¥")
