            override fun onCardRemoved(card: GestureCardWebViewManager.WebViewCardData, position: Int) {
                // å¦‚æœæ²¡æœ‰å¡ç‰‡äº†ï¼Œæ˜¾ç¤ºä¸»é¡µ
