            allContacts.forEach { category ->
                if (category.name != "AIåŠ©æ‰‹" &&
                    category.name != currentGroup &&
                    category.name != "æœªåˆ†ç»„") {
                    options.add("ğŸ“ ç§»åŠ¨åˆ°: ${category.name}")
