                    if (aiContact != null) {
                        // æ·»åŠ AIè”ç³»äººåˆ°å¯¹è¯åˆ—è¡¨
                        addAIContactToCategory(aiContact.name, aiContact.description ?: "AIåŠ©æ‰‹")
