                val source = it.getStringExtra("source")
                if (source?.contains("æ¡Œé¢å°ç»„ä»¶") == true) {
                    Log.d(TAG, "æ”¶åˆ°æ¥è‡ªæ¡Œé¢å°ç»„ä»¶çš„è¯·æ±‚: source=$source")

                    val searchQuery = it.getStringExtra("search_query")
