    private fun createFunctionalHomeTab() {
        try {
            Log.d(TAG, "å¼€å§‹åˆ›å»ºåŠŸèƒ½ä¸»é¡µæ ‡ç­¾é¡µ")
            
            // å…ˆéšè—åŸç”ŸåŠŸèƒ½ä¸»é¡µï¼Œç¡®ä¿ä¸ä¼šæ˜¾ç¤º
            browserHomeContent.visibility = View.GONE
