            }

            // ç¡®ä¿æœç´¢tabä¿æŒé€‰ä¸­çŠ¶æ€ï¼ˆç»¿è‰²ä¸»é¢˜ï¼‰
            updateTabColors()

        } catch (e: Exception) {
