                    }
                }
            } else {
                // æ²¡æœ‰å¯†ç ï¼Œç›´æ¥æ¿€æ´»
                if (group.isHidden) {
                    groupManager.toggleGroupHidden(group.id)
                }
                switchToGroup(group.id, groupManager, skipPasswordCheck = true)
                Toast.makeText(this, "å·²æ¿€æ´»ç»„ï¼š${group.name}", Toast.LENGTH_SHORT).show()
            }
        } catch (e: Exception) {
